         <script>
   function updatePriceInputFields(data) {
      $('#priceWithShipping').val(data.offerPrice);
      $('.offerPrice').val(data.offerPrice);
      $('.feeBuyer').val(data.feeBuyer);
      $('#checkoutFee').val(data.feeBuyer);
      $('.feeSeller').val(data.feeSeller);
   }

   $(document).ready(function () {
      var $finalPrice = $("#finalPrice");
      var $offerPriceShippingChargeTax = $("#offerPrice,#shippingCharge,#tax");
      var $emailId = $("input[name=emailId]");
      var isFirstValidation = true;

      $finalPrice.on("keyup blur", function() {
         var finalPrice = removeNonNumericalChars($finalPrice.val());
         if (isNaN(finalPrice)) finalPrice = 0;

         var shippingCharge = c24.helper.parseNumberByType($("#shippingCharge").val());
         if (isNaN(shippingCharge)) shippingCharge = 0;

         $.getJSON("https://www.chrono24.com/user/checkout-detail.htm?calculateFeeByGivenFinalPrice&callback=?&checkoutId=1606687&emailId=" + $emailId.val() + "&finalPrice=" + finalPrice + "&shippingCharge=" + shippingCharge,
            function (data) {
               updatePriceInputFields(data);
               $("#offerPrice").val(data.offerPriceArticle);
               $("#shippingCharge").val(data.offerPriceShipping);
            }
         );
      });

      $('#tax').on("keyup blur", function() {
         var tax = c24.helper.parseNumberByType($(this).val());

         if(!isNaN(tax) && tax > 0) {
            $finalPrice.prop('disabled',true);
         } else {
            $finalPrice.prop('disabled',false);
         }
      });

      $offerPriceShippingChargeTax.on("keyup blur", function(e) {
         var offerPrice = removeNonNumericalChars($("#offerPrice").val());
         if (isNaN(offerPrice)) offerPrice = 0;

         var shippingCharge = removeNonNumericalChars($("#shippingCharge").val());
         if (isNaN(shippingCharge)) shippingCharge = 0;

         var tax = removeNonNumericalChars($("#tax").val());
         if (isNaN(tax)) tax = 0;

         $.getJSON("https://www.chrono24.com/user/checkout-detail.htm?calculateFee&callback=?&checkoutId=1606687&emailId=" + $emailId.val() + "&offerPrice=" + offerPrice + "&shippingCharge=" + shippingCharge + "&tax=" + tax,
            function (data) {
               updatePriceInputFields(data);
               if (e.type === 'blur' && !isFirstValidation) {
                  if ($('#tax').length > 0) {
                     $('#tax').val(data.offerPriceTax);
                     $('#createOrCorrectOfferForm').data('validator').element('#tax');
                  }
               } else if (e.type === 'blur') {
                  isFirstValidation = false;
               }

               $finalPrice.val(data.finalPrice);
            }
         );
      });

      function removeNonNumericalChars(value) {
         if (typeof value == 'undefined') {
            return 0;
         }

         return c24.helper.parseNumberByType(value);
      }

      var finalPrice = c24.helper.parseNumberByType($finalPrice.val());
      if (isNaN(finalPrice)) finalPrice = 0;
      if(finalPrice > 0) {
         $finalPrice.trigger('keyup');
      } else {
         $offerPriceShippingChargeTax.trigger('keyup');
      }

      $('#jq-add-tax-link').on('click', function () {
         $('button[name="initOfferCorrection"]').click();
      });
   });
</script><script>
         
