<script>
   $(document).ready(function(){
      calculateProgress();
   });

   function calculateProgress () {
      $('.jq-progress-bar-item').each(function (index) {
         var easingTime = 1200;
         var $el = $(this);
         var progress = parseFloat($el.data('progress'));
         progress = progress * 100;

         setTimeout(function () {
            if(index > 0) {
               $el.find('.item-progress-active').css({'width': progress + '%'});

               if (progress > 0) {
                  if($el.data('type') == 'Rating') {
                     addClassDelayed($el.find('.item-progress.dashed'),0,'active');
                  }
               }
            }

            var isTodo = $el.find('[data-has-todo]').length > 0;
            var isComplete = $el.find('[data-complete]').length > 0;
            var isPreview = $el.data('type') == 'Delivery' || $el.data('type') == 'Rating' || $el.data('type') == 'Handover';

            if(isPreview && !isComplete) {
               addClassDelayed($el,0,'is-delivery');
            }

            if (isTodo || isComplete) {
               addClassDelayed($el, isTodo ? easingTime : easingTime/2,'active');
               addAttributeDelayed($el.find('#todo-circle .shape'),easingTime-350,'stroke-dashoffset', 0);
            }

            if (isTodo) {
               addClassDelayed($el.find('[data-has-todo]'),easingTime-300,'has-todo');
            }
         },index*easingTime);
      });
   }

   function addClassDelayed ($el,timeout,cssClass) {
      setTimeout(function () {
         $el.addClass(cssClass);
      },timeout);
   }

   function addAttributeDelayed($el,timeout,attribute, value) {
      setTimeout(function () {
         $el.attr(attribute, value);
      },timeout);
   }
</script>

